sub EVENT_SAY {my $Agnostic = quest::saylink("Agnostic", 1);my $Bertoxxulous = quest::saylink("Bertoxxulous", 1);my $Erollisi_Marr = quest::saylink("Erollisi_Marr", 1);my $Innoruuk = quest::saylink("Innoruuk", 1);my $Karana = quest::saylink("Karana", 1);my $Mithaniel_Marr = quest::saylink("Mithaniel_Marr", 1);my $Rodcet_Nife = quest::saylink("Rodcet_Nife", 1);my $West_Freeport = quest::saylink("West_Freeport", 1);my $South_Qeynos = quest::saylink("South_Qeynos", 1); if ($text=~/Hail/i){$client->Message(14,"Greetings $name. Welcome to the Hall of Sorcery. You have a note for me?");}  if (($text=~/Enchanter/i) && ($ulevel == 1) && ($class eq "Enchanter")) {    quest::say("A Fine choice, the Way of the Enchanter, but I have a few more questions for you. Which Deity did you choose? $Agnostic, $Bertoxxulous, $Erollisi_Marr, $Innoruuk, $Karana, $Mithaniel_Marr or $Rodcet_Nife?");  }if (($text=~/Agnostic/i) && ($ulevel == 1) && ($class eq "Enchanter")) {    quest::say("Following the way of The Agnostic? A Good Choice, So you will not commit to the existence or non-existence of any Deity. This is your choice.");    quest::say("One more question for you $name, What city would you prefer to be sent to? $West_Freeport, or $South_Qeynos.");	}	if (($text=~/Bertoxxulous/i) && ($ulevel == 1) && ($class eq "Enchanter")) {    quest::say("Following the way of Bertoxxulous? A Good Choice, Followers of the Plaguebringer believe the only truth is that everything dies. They view the decay of flesh as a thing of ultimate beauty.");    quest::say("Very well, I  will send you to the Qeynos Catacombs Speak with Reania Jukle, she will guide your path.");	quest::summonitem(18851);	quest::movepc(45,-402,-397,-38.2);	}	  if (($text=~/Erollisi_Marr/i) && ($ulevel == 1) && ($class eq "Enchanter")) {    quest::say("Following the way of The Erollisi Marr? A Good Choice, Followers of The Queen of Love believe that love can conquer all, while not being naive about it.");    quest::say("On your way then, I will send you to West Freeport. Hand this note to Romiak Jusathorn. He is your Guildmaster of Enchanters."); 	quest::summonitem(18786);	quest::movepc(9,-779.2,54.1,-56.1);  }  	if (($text=~/Innoruuk/i) && ($ulevel == 1) && ($class eq "Enchanter")) {    quest::say("Following the way of Innoruuk? A Good Choice, Followers of the Prince of hate believe hate is THE creative force of the universe.It isthe honest belief that if they hate strong enough they could destroy all of Norrath.");    quest::say("Very well, I  will send you to East Freeport Speak with Konious Eranon, he will guide your path.");	quest::summonitem(18851);	quest::movepc(10,-252,-37,-94.2);	}  if (($text=~/Karana/i) && ($ulevel == 1) && ($class eq "Enchanter")) {    quest::say("Following the way of The Karana? A Good Choice, Followers of The Rainkeeper believe in the absolute power of storms.");    quest::say("On your way then, I will send you to South Qeynos. Hand this note to Mespha Tevalian, She is your Guildmaster of Enchanters."); 	quest::summonitem(119871);	quest::movepc(1,-654,305,3.7);  }   if (($text=~/Mithaniel_Marr/i) && ($ulevel == 1) && ($class eq "Enchanter")) {    quest::say("Following the way of Innoruuk? A Good Choice, Followers of the Lightbringer belive that valor is what separates civilized beings from the beast. They have a strict moral code that prizes truth, honor and charity.");    quest::say("On your way then, I will send you to West Freeport. Hand this note to Romiak Jusathorn. He is your Guildmaster of Enchanters."); 	quest::summonitem(18741);	quest::movepc(9,-779.2,54.1,-56.1);	}	if (($text=~/Rodcet_Nife/i) && ($ulevel == 1) && ($class eq "Enchanter")) {    quest::say("Following the way of The Rodcet Nife? A Good Choice, Followers of The Prime Healer take oath to fight disease and death until one or the other takes them.");    quest::say("On your way then, I will send you to South Qeynos. Hand this note to Mespha Tevalian, She is your Guildmaster of Enchanters."); 	quest::summonitem(119871);	quest::movepc(1,-654,305,3.7);  }  if (($text=~/West_Freeport/i) && ($ulevel == 1) && ($class eq "Enchanter")) {    quest::say("On your way then, I will send you to West Freeport. Hand this note to Romiak Jusathorn. He is your Guildmaster of Enchanters."); 	quest::summonitem(18741);	quest::movepc(9,-779.2,54.1,-56.1);	}  if (($text=~/South_Qeynos/i) && ($ulevel == 1) && ($class eq "Enchanter")) {    quest::say("On your way then, I will send you to South Qeynos. Hand this note to Mespha Tevalian, She is your Guildmaster of Enchanters."); 	quest::summonitem(119871);	quest::movepc(1,-654,305,3.7);	}  }sub EVENT_ITEM {my $Enchanter = quest::saylink("Enchanter", 1);if (plugin::check_handin(\%itemcount, 18720 => 1) && $class eq "Enchanter") {   quest::say("I am Mespha Tevalian of the Hall of Sorcery. A few years ago, The Rathe server was created in order to bring back the feel of the orginal EQ zones. Many things have changed over the years and with those changes, some things could not be done anymore.");    quest::say("You were sent to me because there is a problem with getting you to the correct city with the correct guild summons.");	quest::say("Since you are talking to me I must assume you are a $Enchanter?");	}elsif ($item1=="119871"){$client->Message(14,"Greetings $name. Welcome to the Hall of Sorcery. You have much to learn. Wear this robe and represent us well. See my apprentice, Unsar Koldhagon, he will get you started and help you with your studies.");quest::summonitem("13508");quest::ding();quest::faction(135, 10);quest::faction(240, 10);quest::faction(21, -30);quest::faction(235, -30);quest::exp(1000);quest::rebind(1,-654,305,3.7);}	else {	quest::say("I cant use this.");    plugin::return_items(\%itemcount); }#END of FILE Zone:qeynos ID:1068 -- Mespha_Tevalian
